import React from 'react';
import MainPage from './pages/MainPage.js';
import CreateGamePage from './pages/CreateGamePage.js';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
// import ListHeader from "../components/ListHeader.js";
// import BalanceList from "../components/BalanceList.js";
import styled from '@emotion/styled';

const Form = styled.form`
  width: 400px;
  margin: 0 auto;
  height: 300px;
  background-color: #EBEBF6;
`
const ListForm = styled.form`
  width: 90%;
  height: 65%;
  display: flex;
  justify-content: space-around; 
  align-content: space-between;
  margin: auto auto;
  //background-color: darkgray;
`

function App() {
  return (
    <Router>
      <Switch>
        <Route path="/" exact component={MainPage} />
        <Route path="/new" component={CreateGamePage} />
      </Switch>
      <Form>
        {/* <ListHeader title="제목이엄청나게 길 경우"/> */}
        <ListForm>
          {/* <BalanceList /> */}
        </ListForm>
      </Form>
    </Router>
  );
}

export default App;
